    {# Render each radio button manually #}
    {% for child in form.nbstar %}
        <input type=\"radio\" id=\"star{{ child.vars.value }}\" name=\"{{ child.vars.full_name }}\" value=\"{{ child.vars.value }}\" class=\"star-input\" {{ child.vars.checked ? 'checked=\"checked\"' : '' }}/>
